/**
 * Basic GUID generator
 * from http://stackoverflow.com/questions/105034/create-guid-uuid-in-javascript
 * available under the terms of CC-BY-SA
 * @guid
 * @return {String}
 */
const guid = function() {
    /**
     * Format helper
     * @s4
     * @return {String}
     */
    function s4() {
        return Math.floor((1 + Math.random()) * 0x10000)
            .toString(16)
            .substring(1);
    }
    return s4() + s4() + '-' + s4() + '-' + s4() + '-' +
        s4() + '-' + s4() + s4() + s4();
};